(function($){$.fn.fontadjust=function(options){var settings=$.extend({smalleras:10,biggeras:118,fontsizesmall:"13px",fontsizebig:"1px",fontsizeother:"9px"},options);return this.each(function(){var text=$(this).text().length;if(text<=settings.smalleras)$(this).css("font-size",settings.fontsizesmall);else if(text>=settings.biggeras)$(this).css("font-size",settings.fontsizebig);else $(this).css("font-size",settings.fontsizeother)})}})(jQuery);